let state = flow.get('resend_state');
let tempS = flow.get('resend_temp');
let fanS  = flow.get('resend_fan');

if (msg.from_mqtt === true) {

    let msg3 = null;

    // Resend message if setting was not applied
    if (fanS !== undefined) {
        let fan = flow.get('fan');
        if (fan === fanS) {
            //node.warn(`match fan ${fan}`)
            flow.set('resend_fan', undefined);
        }
        else {
            //node.warn(`resend fan ${fanS}`)
            flow.set('fan', fanS);
            msg3 = { payload: true};
        }
    }
    if (tempS !== undefined) {
        let temp = flow.get('temp');
        if (temp === tempS) {
            //node.warn(`match temp ${temp}`)
            flow.set('resend_temp', undefined);
        }
        else {
            //node.warn(`resend temp ${tempS}`)
            flow.set('temp', tempS);
            msg3 = { payload: true};
        }
    }
    if (state !== undefined) {
        if (msg.payload === state) {
            //node.warn('match switch')
            flow.set('resend_state', undefined)
        }
        else if (state == true) {
            //node.warn('resend switch on')
            msg3 = { payload: true};
        }
        else {
            //node.warn('resend switch off')
            msg3 = { payload: false};
        }
    }

    //node.warn(`msg3 = ${msg3}`)
    return [null, null, msg3];

}
else if (msg.payload === true) {

    flow.set('switch', true);
    flow.set('resend_state', true);

    let temp = flow.get('temp');
    let fan = flow.get('fan');

    if (temp === undefined) {
        temp = 20;
    }

    if (fan === undefined) {
        fan = "eco";
    }

    // Prepare two different messages
    let msg1 = { payload: temp };  // Message for the first output
    let msg2 = { payload: fan };  // Message for the second output
    return [msg1, msg2, null];

}
else {

    flow.set('switch', false);
    flow.set('resend_state', false);

    let msg1 = {payload: 0}
    let msg2 = { payload: "off" };
    return [msg1, msg2, null];

}

