let val = +msg.payload;  // Convert to an integer

let low     = env.get('low');
let high    = env.get('high');
let thr     = env.get('threshold');
let message = env.get('message');
let unit    = env.get('unit');

let lastAlarm = context.get('lastAlarm') || false;
let alarm = false;

if (val < low) {
    alarm = 'low';
}
else if (val > high) {
    alarm = 'high';
}
else if (val > (low + thr) && val < (high - thr)) {
    alarm = false;
    context.set('lastAlarm', alarm);
}
//node.warn(`val=${val}, low=${low}, high=${high}, thr=${thr}, alarm=${alarm}, lastAlarm=${lastAlarm}`);

if (alarm && alarm != lastAlarm) {
    context.set('lastAlarm', alarm);
    msg.payload = `⚠️ Caravan alert!\n${message}: ${val} ${unit}`;
    node.warn(msg.payload);
    return msg;
}
else {
    return null;
}

let temp = msg.payload;

offset   = env.get('offset');
rounding = env.get('rounding');

temp = temp + offset;

msg.payload = +(+temp).toFixed(rounding);

return msg;

// Resend the message if setting has not been applied

topic = env.get('topic');
state = context.get('state');

const DEBUG = false;

if (msg.topic === topic) {

    if (DEBUG) node.warn(`Sent ${msg.payload}`);
    context.set('state', msg.payload);
    return [null, msg];
}
else {

    if (state !== undefined) {

        if (msg.payload === state) {
            if (DEBUG) node.warn(`Match ${msg.payload}`);
            context.set('state', undefined);
            return [msg, null];
        }
        else {
            msg.payload = state;
            if (DEBUG) node.warn(`Resend ${msg.payload}`);
            return [null, msg];
        }

    }
    else {
        return [msg, null];
    }

}

empty = env.get('empty');
full  = env.get('full');

level = msg.payload;

percent = 100 * (level - empty) / (full - empty);

if (percent > 100) {
    percent = 100;
}

if (percent < 0) {
    percent = 0;
}

msg.payload = percent.toFixed(0);

return msg;

let totalSeconds = msg.payload;

let days = Math.floor(totalSeconds / 86400); // 86400 seconds in a day
let hours = Math.floor((totalSeconds % 86400) / 3600); // Remaining hours

if (totalSeconds === null ) {
    msg.payload = `--`;
}
else {
    msg.payload = `${days} d &nbsp;${hours} h`;
}

return msg;

let kwh = msg.payload;

msg.payload = kwh * 1000;
return msg;

// Get the stored array or initialize if it doesn't exist
let values = context.get('values') || [];

// Add the new payload
values.push(msg.payload);

// Keep only the last 10 values
if (values.length > 5) {
    values.shift(); // Remove the oldest value
}

// Store the updated array back to context
context.set('values', values);

// Find the maximum value
let max = Math.max(...values);

// Send the maximum value
msg.payload = max;
return msg;

